pragma solidity >=0.8.17;

interface IPriceOracle {
    struct Price {
        uint256 base;
        uint256 premium;
    }

    /**
     * @dev Returns the price to register or renew a name.
     * @param name The name being registered or renewed.
     * @param expires When the name presently expires (0 if this is a new registration).
     * @param duration How long the name is being registered or extended for, in seconds.
     * @return base premium tuple of base price + premium price
     */
    function price(
        string calldata name,
        uint256 expires,
        uint256 duration
    ) external view returns (Price calldata);
}
","keccak256":"0xb467ac8727e0b11c3af599082d36c09d0cc9c27479eb0595603425210b92c9b9","license":"MIT"},"contracts/interfaces/IRegistrar.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "../libs/IERC721Enumerable.sol";

interface IRegistrar is IERC721Enumerable {
    event ControllerAdded(address indexed controller);
    event ControllerRemoved(address indexed controller);
    event NameMigrated(uint256 indexed id, address indexed owner, uint256 expires);
    event NameRegistered(uint256 indexed id, address indexed owner, uint256 expires);
    event NameRenewed(uint256 indexed id, uint256 expires);

    // Returns the expiration timestamp of the specified label hash.
    function nameExpires(uint256 id) external view returns (uint256);

    // Returns true iff the specified name is available for registration.
    function available(uint256 id) external view returns (bool);

    // Get the name of a registered id
    function nameOf(uint256 id) external view returns (string memory);

    // Get the token id by node
    function getIdByNode(bytes32 node) external view returns (uint256 id);

    // Get the name by node
    function getNameByNode(bytes32 node) external view returns (string memory);

    /**
     * @dev Register a name.
     */
    function register(string calldata name, address owner, uint256 duration, address resolver)
        external
        returns (uint256);

    function renew(uint256 id, uint256 duration) external returns (uint256);

    /**
     * @dev Reclaim ownership of a name in FNS, if you own it in the registrar.
     */
    function reclaim(uint256 id, address owner) external;
}
","keccak256":"0x975bd0717b923157010f6ab04cbee61e6033c047e0ee92d1dc91c42b54fcba6b","license":"MIT"},"contracts/interfaces/IRegistrarController.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./IPriceOracle.sol";

interface IRegistrarController {
    /********************* events *********************/
    event NameRegistered(
        string name,
        bytes32 indexed label,
        address indexed owner,
        uint256 baseCost,
        uint256 premium,
        uint256 expires
    );
    event NameRenewed(string name, bytes32 indexed label, uint256 cost, uint256 expires);
    event MinLengthUpdated(uint256 oldMinLen, uint256 newMinLen);

    /********************* errors *********************/
    error NameNotAvailable(string name);
    error DurationTooShort(uint256 duration);
    error ResolverRequiredWhenDataSupplied();
    error InsufficientValue();
    error InvalidExpirationTime();
    error InvalidRecipient();
    error InvalidNameLength();
    error RegisterCountLimited();

    /********************* functions *********************/
    function valid(string memory name) external view returns (bool);

    function available(string memory name) external view returns (bool);

    function nameExpires(string memory name) external view returns (uint256);

    function rentPrice(string memory name, uint256 duration) external view returns (IPriceOracle.Price memory);

    function register(
        string calldata name,
        address owner,
        uint256 duration,
        address resolver,
        bytes[] calldata data,
        bool reverseRecord
    ) external payable;

    function renew(string calldata name, uint256 duration) external payable;

